// src/pages/ManageReservation.js
import React, { useEffect, useState } from 'react';
import axios from 'axios';

const Manage = () => {
    const [reservations, setReservations] = useState([]);
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchReservations = async (date) => {
            try {
                const token = localStorage.getItem('jwt');
                const response = await axios.post('http://localhost:3000/manage', {
                    headers: { Authorization: `Bearer ${token}` },
                });
                console.log(response)// Clear selection when fetching new computers
            } catch (error) {
                console.error('Error fetching available computers:', error);
                alert('Failed to fetch available computers. Please try again later.'); // Informative error message
            }
        };



        fetchReservations();
    }, []);

    if (loading) {
        return <div>Loading reservations...</div>;
    }

    if (error) {
        return <div>{error}</div>;
    }

    return (
        <div className="manage-reservation">
            <h1>Manage Your Reservations</h1>
            {reservations.length > 0 ? (
                <ul>
                    {reservations.map((reservation) => (
                        <li key={reservation.id}>
                            {`Reservation ID: ${reservation.id}, Date: ${reservation.date}, Status: ${reservation.status}`}
                        </li>
                    ))}
                </ul>
            ) : (
                <p>No reservations found.</p>
            )}
        </div>
    );
};

export default Manage;
