// src/pages/ManageReservation.js
import React, { useEffect, useState } from 'react';
import axios from 'axios';

const Manage = () => {
    const [reservations, setReservations] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchReservations = async () => {
            try {
                // Get the JWT token from local storage
                const token = localStorage.getItem('token'); 

                const response = await axios.get('http://localhost:3000/manage', {
                    headers: {
                        Authorization: `Bearer ${token}`, // Include the token in the Authorization header
                    },
                });
                setReservations(response.data);
            } catch (err) {
                setError('Error fetching reservations');
            } finally {
                setLoading(false);
            }
        };

        fetchReservations();
    }, []);

    if (loading) {
        return <div>Loading reservations...</div>;
    }

    if (error) {
        return <div>{error}</div>;
    }

    return (
        <div className="manage-reservation">
            <h1>Manage Your Reservations</h1>
            {reservations.length > 0 ? (
                <ul>
                    {reservations.map((reservation) => (
                        <li key={reservation.id}>
                            {`Reservation ID: ${reservation.id}, Date: ${reservation.date}, Status: ${reservation.status}`}
                        </li>
                    ))}
                </ul>
            ) : (
                <p>No reservations found.</p>
            )}
        </div>
    );
};

export default Manage;
