import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Routes, Navigate } from 'react-router-dom';
import Login from './pages/Login/Login';
import Register from './pages/Register/Register';
import Reserve from './pages/Reserve/Reserve';
import Header from './components/Header';
import Slideshow from './components/Slideshow';
import Manage from './pages/Manage/Manage'; // Trang người dùng bình thường
import Admin from './pages/Admin/Admin'; // Trang admin
import NotFound from './pages/NotFound/NotFound';

const App = () => {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [usfId, setUsfId] = useState('');
  const [role, setRole] = useState('');

  const handleLogin = (usfId, role) => {
    setIsLoggedIn(true);
    setUsfId(usfId);
    setRole(role);
  };

  const handleLogout = () => {
    localStorage.removeItem('jwt');
    setIsLoggedIn(false);
    setUsfId('');
    setRole('');
  };

  useEffect(() => {
    const token = localStorage.getItem('jwt');
    if (token) {
      const userData = JSON.parse(atob(token.split('.')[1]));
      setUsfId(userData.usf_id);
      setIsLoggedIn(true);
      setRole(userData.usf_role);
    }
  }, []);

  return (

    <Router>
      <Header isLoggedIn={isLoggedIn} usfId={usfId} onLogout={handleLogout} />
      h1
      <Routes>
        <Route path="/login" element={<Login onLogin={handleLogin} />} />
        <Route path="/register" element={<Register />} />
        <Route path="/reserve" element={role === 'admin' ? <Navigate to="/admin" /> : <Reserve />} /> {/* Ngăn admin truy cập vào trang Reserve */}
        <Route path="/manage" element={role === 'admin' ? <Navigate to="/admin" /> : <Manage />} /> {/* Ngăn admin truy cập vào trang Manage */}
        <Route path="/admin" element={role === 'admin' ? <Admin /> : <Navigate to="/login" />} /> {/* Chỉ cho phép admin vào trang admin */}
        <Route path="/" element={<Slideshow />} />
        <Route path="*" element={<NotFound />} />
      </Routes>
    </Router>
  );
};

export default App;
