import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const Reserve = () => {
    const [availableComputers, setAvailableComputers] = useState([]);
    const [selectedDate, setSelectedDate] = useState(""); // State to store the selected date
    const [isAvailableChecked, setIsAvailableChecked] = useState(false); // State to track availability check
    const navigate = useNavigate();

    useEffect(() => {
        const token = localStorage.getItem('jwt');
        if (!token) {
            navigate('/login');
        }
    }, [navigate]);

    const fetchAvailableComputers = async (date) => {
        try {
            const token = localStorage.getItem('jwt');
            const response = await axios.post('http://localhost:3000/available-computers', { date }, {
                headers: {
                    Authorization: `Bearer ${token}`,
                },
            });
            const formattedComputers = response.data.map(item => `Computer #${item.computer_id}`);
            console.log(formattedComputers);
            setAvailableComputers(formattedComputers); // Store the list of available computers
            setIsAvailableChecked(true); // Set to true after checking availability
        } catch (error) {
            console.error('Error fetching available computers:', error);
        }
    };

    const handleDateChange = (event) => {
        setSelectedDate(event.target.value); // Update the selected date
    };

    const handleSubmit = (event) => {
        event.preventDefault(); // Prevent default action
        if (selectedDate) {
            fetchAvailableComputers(selectedDate); // Call function to fetch available computers
        }
    };

    const handleComputerSelect = (computer) => {
        // Handle the computer selection here (e.g., store it in state or navigate to another page)
        console.log(`Selected: ${computer}`);
        // You can perform additional actions like navigating or showing confirmation
    };

    return (
        <div>
            <h1>Reserve Page</h1>
            <form onSubmit={handleSubmit}>
                <label>
                    Select Date:
                    <input type="date" value={selectedDate} onChange={handleDateChange} required />
                </label>
                <button type="submit">Check Availability</button>
            </form>

            {isAvailableChecked && (
                <div>
                    <h2>Available Computers for {selectedDate}:</h2>
                    <ul>
                        {availableComputers.length > 0 ? (
                            availableComputers.map((computer, index) => (
                                <li key={index}>
                                    <button onClick={() => handleComputerSelect(computer)}>{computer}</button> {/* Clickable computer name */}
                                </li>
                            ))
                        ) : (
                            <li>No computers available.</li> // Message if no computers are available
                        )}
                    </ul>
                </div>
            )}
        </div>
    );
};

export default Reserve;
