// src/pages/Reserve/Reserve.js
import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

const Reserve = () => {
  const navigate = useNavigate();
  const [selectedDate, setSelectedDate] = useState('');
  const [availableComputers, setAvailableComputers] = useState([]);

  useEffect(() => {
    const token = localStorage.getItem('jwt');
    if (!token) {
      navigate('/login'); // Nếu không có token, chuyển hướng đến trang login
    }
  }, [navigate]);

  const handleDateChange = (e) => {
    setSelectedDate(e.target.value);
  };

  const fetchAvailableComputers = async () => {
    try {
      const response = await axios.post('/available-computers', { date: selectedDate });
      setAvailableComputers(response.data); // Lưu danh sách máy tính khả dụng
    } catch (error) {
      console.error('Error fetching available computers:', error);
    }
  };

  const handleCheckAvailability = () => {
    if (selectedDate) {
      fetchAvailableComputers(); // Gọi hàm để lấy máy tính khả dụng
    } else {
      alert('Please select a date first.'); // Thông báo nếu chưa chọn ngày
    }
  };

  return (
    <div>
      <h1>Reserve Page</h1>
      <label htmlFor="date">Select Date:</label>
      <input
        type="date"
        id="date"
        value={selectedDate}
        onChange={handleDateChange}
      />
      <button onClick={handleCheckAvailability}>Check Availability</button>

      {availableComputers.length > 0 && (
        <div>
          <h2>Available Computers:</h2>
          <ul>
            {availableComputers.map((computer) => (
              <li key={computer.computer_id}>
                {`Computer ID: ${computer.computer_id}, Status: ${computer.status}`}
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
};

export default Reserve;
