import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const Reserve = () => {
    const [availableComputers, setAvailableComputers] = useState([]);
    const [selectedDate, setSelectedDate] = useState(""); // State to store the selected date
    const navigate = useNavigate();

    useEffect(() => {
        const token = localStorage.getItem('jwt');
        if (!token) {
            navigate('/login');
        }
    }, [navigate]);

    const fetchAvailableComputers = async (date) => {
        try {
            const token = localStorage.getItem('jwt');
            const response = await axios.post('http://localhost:3000/available-computers', { date }, {
                headers: {
                    Authorization: `Bearer ${token}`,
                },
            });
            const formattedComputers = response.data.map(item => `Computer #${item.computer_id}`);
            setAvailableComputers(formattedComputers); // Store the list of available computers
        } catch (error) {
            console.error('Error fetching available computers:', error);
        }
    };

    const handleDateChange = (event) => {
        setSelectedDate(event.target.value); // Update the selected date
    };

    const handleSubmit = (event) => {
        event.preventDefault(); // Prevent default action
        if (selectedDate) {
            fetchAvailableComputers(selectedDate); // Call the function to fetch available computers
        }
    };

    return (
        <div>
            <h1>Reserve Page</h1>
            <form onSubmit={handleSubmit}>
                <label>
                    Select Date:
                    <input type="date" value={selectedDate} onChange={handleDateChange} required />
                </label>
                <button type="submit">Check Availability</button>
            </form>
            <h2>Available Computers for {selectedDate}:</h2>
            <ul>
                {availableComputers.length > 0 ? (
                    availableComputers.map((computer, index) => (
                        <li key={index}>{computer}</li> // Render the formatted computer strings
                    ))
                ) : (
                    <li>No computers available.</li> // Message if no computers are available
                )}
            </ul>
        </div>
    );
};

export default Reserve;
