import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const Reserve = () => {
    const [availableComputers, setAvailableComputers] = useState([]);
    const navigate = useNavigate();

    // Giả sử bạn đã thiết lập một ngày cụ thể
    const specificDate = "2024-11-01"; // Thay đổi thành ngày bạn muốn kiểm tra

    useEffect(() => {
        const token = localStorage.getItem('jwt');
        if (!token) {
            navigate('/login');
        } else {
            fetchAvailableComputers(specificDate);
        }
    }, [navigate]);

    const fetchAvailableComputers = async (date) => {
        try {
            const token = localStorage.getItem('jwt');
            const response = await axios.post('/available-computers', { date }, {
                headers: {
                    Authorization: `Bearer ${token}`,
                },
            });
            setAvailableComputers(response.data); // Lưu danh sách máy tính khả dụng
        } catch (error) {
            console.error('Error fetching available computers:', error);
        }
    };

    return (
        <div>
            <h1>Reserve Page</h1>
            <h2>Available Computers for {specificDate}:</h2>
            <ul>
                {availableComputers.map((computer) => (
                    <li key={computer.computer_id}>{computer.computer_name}</li>
                ))}
            </ul>
        </div>
    );
};

export default Reserve;
