import React, { useEffect, useState } from 'react';
import axios from 'axios';

const Manage = () => {
    const [reservations, setReservations] = useState([]);
    const [error, setError] = useState('');

    // Fetch reservations for the logged-in user
    const fetchReservations = async () => {
        try {
            const token = localStorage.getItem('jwt'); // Retrieve token from local storage
            const usfId = localStorage.getItem('usf_id'); // Retrieve usf_id from local storage

            const response = await axios.get(`http://localhost:3000/manage?usf_id=${usfId}`, {
                headers: { Authorization: `Bearer ${token}` },
            });
            setReservations(response.data); // Set the fetched reservations to state
        } catch (err) {
            console.error('Error fetching reservations:', err);
            setError('Error fetching reservations');
        }
    };

    useEffect(() => {
        fetchReservations(); // Fetch reservations on component mount
    }, []);

    return (
        <div className="manage-reservation">
            <h1>Manage Your Reservations</h1>
            {error && <p>{error}</p>}
            <ul>
                {reservations.map((reservation) => (
                    <li key={reservation.id}>
                        {/* Render data for each reservation */}
                        Date: {reservation.date} - Status: {reservation.status}
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default Manage;
