// src/components/Login.js
import React, { useState } from 'react';
import axios from '../../'; // Đảm bảo bạn đã tạo axiosConfig.js
import './Login.css'; 

const Login = () => {
    const [showPassword, setShowPassword] = useState(false);
    const [password, setPassword] = useState('');
    const [usfEmail, setUsfEmail] = useState('');

    const togglePasswordVisibility = () => {
        setShowPassword(!showPassword);
    };

    const handleLogin = async (e) => {
        e.preventDefault();
        try {
            const response = await axios.post('/login', { usf_email: usfEmail, password });
            console.log('Login successful:', response.data);
            // Xử lý token hoặc thông tin người dùng ở đây
        } catch (error) {
            console.error('Login failed:', error);
            // Có thể hiển thị thông báo lỗi cho người dùng
        }
    };

    return (
        <div className="container">
            <form className="login-form" onSubmit={handleLogin}>
                <h2>Login</h2>
                <div className="form-group">
                    <label htmlFor="email">USF Email</label>
                    <input 
                        type="email" 
                        id="email" 
                        placeholder="Enter your usf email (@usf.edu)" 
                        required 
                        value={usfEmail}
                        onChange={(e) => setUsfEmail(e.target.value)}
                    />
                </div>
                <div className="form-group">
                    <label htmlFor="password">Password</label>
                    <div style={{ position: 'relative' }}>
                        <input
                            type={showPassword ? 'text' : 'password'}
                            id="password"
                            value={password}
                            onChange={(e) => setPassword(e.target.value)}
                            placeholder="Enter your password"
                            required
                        />
                        <button
                            type="button"
                            className="show-password"
                            onClick={togglePasswordVisibility}
                        >
                            {showPassword ? 'Hide' : 'Show'}
                        </button>
                    </div>
                </div>
                <button type="submit" className="login-button">Login</button>
                <div className="forgot-password">
                    <a href="#">Forgot password?</a>
                </div>
            </form>
        </div>
    );
};

export default Login;
