import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Routes, Navigate } from 'react-router-dom';
import Login from './pages/Login/Login';
import Register from './pages/Register/Register';
import Reserve from './pages/Reserve/Reserve';
import Header from './components/Header';
import Slideshow from './components/Slideshow';
import Manage from './pages/Manage/Manage';
import NotFound from './pages/NotFound/NotFound';
import Dashboard from './pages/Dashboard/Dashboard';

const App = () => {
    const [isLoggedIn, setIsLoggedIn] = useState(false);
    const [usfId, setUsfId] = useState('');
    const [role, setRole] = useState('');

    useEffect(() => {
        const token = localStorage.getItem('jwt');
        if (token) {
            const userData = JSON.parse(atob(token.split('.')[1]));
            setUsfId(userData.usf_id);
            setRole(userData.usf_role);
            setIsLoggedIn(true);
        }
    }, []);

    const handleLogin = (usfId) => {
        setIsLoggedIn(true);
        setUsfId(usfId);
    };

    const handleLogout = () => {
        localStorage.removeItem('jwt');
        setIsLoggedIn(false);
        setUsfId('');
        setRole('');
    };

    const renderRoute = (element, condition, redirectTo) => (
        condition ? element : <Navigate to={redirectTo} />
    );

    return (
        <Router>
            <Header isLoggedIn={isLoggedIn} usfId={usfId} usfRole={role} onLogout={handleLogout} />
            <Routes>
                <Route path="/login" element={<Login onLogin={handleLogin} />} />
                <Route path="/register" element={<Register />} />
                <Route path="/reserve" element={renderRoute(<Reserve />, role !== 'admin', "/dashboard")} />
                <Route path="/manage" element={renderRoute(<Manage />, role !== 'admin', "/dashboard")} />
                <Route path="/" element={<Slideshow role={role} />} />
                <Route path="/dashboard" element={renderRoute(<Dashboard />, role === 'admin', "/")} />
                <Route path="*" element={<NotFound />} />
            </Routes>
        </Router>
    );
};

export default App;
