// src/components/Login.js
import React, { useState } from 'react';
import Slideshow from './Slideshow'; // Import the Slideshow component
import './Login.css'; // Import CSS for styling

const Login = () => {
  const [usfEmail, setUsfEmail] = useState('');
  const [password, setPassword] = useState('');

  const handleLogin = (e) => {
    e.preventDefault();
    // Add your login logic here (e.g., API call)
    console.log("Logging in with:", { usfEmail, password });
  };

  return (
    <div>
      <Slideshow />
      <div className="container"> {/* Centering container */}
        <div className="login-form">
          <h2>Login</h2>
          <form onSubmit={handleLogin}>
            <div className="form-group">
              <label htmlFor="usf-email">USF Email:</label>
              <input
                type="email"
                id="usf-email"
                value={usfEmail}
                onChange={(e) => setUsfEmail(e.target.value)}
                required
                placeholder="example@usf.edu"
              />
            </div>
            <div className="form-group">
              <label htmlFor="password">Password:</label>
              <input
                type="password"
                id="password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                required
                placeholder="Enter your password"
              />
            </div>
            <button type="submit" className="login-button">Login</button>
          </form>
          <div className="forgot-password">
            <a href="/forgot-password">Forgot your password?</a>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Login;
