// src/pages/Reserve/Reserve.js
import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

const Reserve = () => {
  const navigate = useNavigate();
  const [date, setDate] = useState('');
  const [availableComputers, setAvailableComputers] = useState([]);

  useEffect(() => {
    const token = localStorage.getItem('jwt');
    if (!token) {
      navigate('/login'); // Nếu không có token, chuyển hướng đến trang login
    }
  }, [navigate]);

  const handleDateChange = (event) => {
    setDate(event.target.value); // Cập nhật trạng thái ngày
  };

  const fetchAvailableComputers = async () => {
    if (!date) {
      alert('Vui lòng chọn một ngày trước khi xem máy tính khả dụng.'); // Thông báo nếu chưa chọn ngày
      return;
    }

    try {
      const response = await axios.post('/available-computers', { date_time: date, }); // Gửi ngày đến server
      setAvailableComputers(response.data); // Lưu danh sách máy tính khả dụng
    } catch (error) {
      console.error('Error fetching available computers:', error);
    }
  };

  const handleReserve = async (computer_id) => {
    try {
      const response = await axios.post('/reserve', { date, computer_id });
      alert(response.data.message); // Hiển thị thông báo khi đặt chỗ thành công
    } catch (error) {
      console.error('Error making reservation:', error);
      alert('Error making reservation: ' + error.response.data.message);
    }
  };

  return (
    <div>
      <h1>Reserve Page</h1>
      <label htmlFor="date">Choose a date:</label>
      <input
        type="date"
        id="date"
        value={date}
        onChange={handleDateChange}
      />
      <button onClick={fetchAvailableComputers}>Xem Máy Tính Khả Dụng</button>

      <h2>Available Computers:</h2>
      <ul>
        {availableComputers.map((computer) => (
          <li key={computer.computer_id}>
            {computer.computer_id} - {computer.status}
            <button onClick={() => handleReserve(computer.computer_id)}>Reserve</button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Reserve;
