// src/App.js
import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Routes, useNavigate } from 'react-router-dom';
import Login from './pages/Login/Login';
import Register from './pages/Register/Register';
import Reserve from './pages/Reserve/Reserve';
import Header from './components/Header';
import Slideshow from './components/Slideshow';
import Manage from './pages/Manage/Manage';

const App = () => {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [usfId, setUsfId] = useState('');
  const [role, setRole] = useState(''); // Thêm state cho role

  const handleLogin = (usfId, userRole) => {
    setIsLoggedIn(true);
    setUsfId(usfId);
    setRole(userRole); // Cập nhật role khi đăng nhập
  };

  const handleLogout = () => {
    localStorage.removeItem('jwt');
    localStorage.removeItem('usf_id'); // Xóa usf_id khi đăng xuất
    localStorage.removeItem('role'); // Xóa role khi đăng xuất
    setIsLoggedIn(false);
    setUsfId('');
    setRole(''); // Reset role
  };

  useEffect(() => {
    const token = localStorage.getItem('jwt');
    if (token) {
      const userData = JSON.parse(atob(token.split('.')[1]));
      setUsfId(userData.usf_id);
      setRole(userData.role); // Cập nhật role từ token
      setIsLoggedIn(true);
    }
  }, []);

  return (
    <Router>
      <Header isLoggedIn={isLoggedIn} usfId={usfId} role={role} onLogout={handleLogout} /> {/* Gửi role cho Header */}
      <Routes>
        <Route path="/login" element={<Login onLogin={handleLogin} />} />
        <Route path="/register" element={<Register />} />
        <Route path="/reserve" element={<Reserve />} />
        <Route path="/manage" element={<Manage />} />
        <Route path="/" element={<Slideshow />} />
      </Routes>
    </Router>
  );
};

export default App;
