import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

const Reserve = () => {
  const navigate = useNavigate();
  const [selectedDate, setSelectedDate] = useState('');
  const [availableComputers, setAvailableComputers] = useState([]);
  const [selectedComputer, setSelectedComputer] = useState(null);

  useEffect(() => {
    const token = localStorage.getItem('jwt');
    if (!token) {
      navigate('/login'); // Chuyển hướng đến trang login nếu không có token
    }
  }, [navigate]);

  const fetchAvailableComputers = async (date) => {
    const token = localStorage.getItem('jwt'); // Lấy token từ local storage
    try {
        const response = await axios.post('/available-computers', { date }, {
            headers: {
                Authorization: `Bearer ${token}`, // Thêm token vào header
            },
        });
        setAvailableComputers(response.data); // Lưu danh sách máy tính khả dụng
    } catch (error) {
        console.error('Error fetching available computers:', error);
        // Bạn có thể xử lý lỗi cụ thể ở đây, ví dụ: hiển thị thông báo cho người dùng
    }
};


  const handleDateChange = (event) => {
    const date = event.target.value;
    setSelectedDate(date);
    if (date) {
      fetchAvailableComputers(date); // Gửi ngày đến server ngay khi chọn ngày
    } else {
      setAvailableComputers([]); // Nếu không có ngày, reset danh sách máy tính
    }
  };

  const handleComputerSelect = (computerId) => {
    setSelectedComputer(computerId);
  };

  const handleReserve = async () => {
    if (!selectedDate || !selectedComputer) {
      alert('Please select a date and a computer.');
      return;
    }

    try {
      const response = await axios.post('/reserve', {
        date: selectedDate,
        computer_id: selectedComputer,
      });
      alert(response.data.message); // Hiển thị thông báo từ server
      // Xử lý thêm nếu cần sau khi đặt chỗ thành công
    } catch (error) {
      console.error('Error reserving computer:', error);
      alert('Failed to reserve computer. Please try again.');
    }
  };

  return (
    <div>
      <h1>Reserve a Computer</h1>
      <label htmlFor="date">Select Date:</label>
      <input type="date" id="date" value={selectedDate} onChange={handleDateChange} />

      {availableComputers.length > 0 && (
        <div>
          <h2>Available Computers</h2>
          <ul>
            {availableComputers.map((computer) => (
              <li key={computer.computer_id}>
                <label>
                  <input
                    type="radio"
                    name="computer"
                    value={computer.computer_id}
                    onChange={() => handleComputerSelect(computer.computer_id)}
                  />
                  Computer ID: {computer.computer_id} - Status: {computer.status}
                </label>
              </li>
            ))}
          </ul>
        </div>
      )}

      <button onClick={handleReserve}>Reserve Computer</button>
    </div>
  );
};

export default Reserve;
