// src/App.js
import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Routes, Navigate } from 'react-router-dom';
import Login from './pages/Login/Login';
import Register from './pages/Register/Register';
import Reserve from './pages/Reserve/Reserve';
import Header from './components/Header';
import Slideshow from './components/Slideshow';

const App = () => {
    const [isLoggedIn, setIsLoggedIn] = useState(false);
    const [usfId, setUsfId] = useState('');

    useEffect(() => {
        // Kiểm tra token trong localStorage khi ứng dụng khởi động
        const token = localStorage.getItem('jwt');
        if (token) {
            const userData = JSON.parse(atob(token.split('.')[1]));
            setUsfId(userData.usf_id);
            setIsLoggedIn(true);
        }
    }, []);

    const handleLogin = (usfId) => {
        setUsfId(usfId);
        setIsLoggedIn(true);
    };

    const handleLogout = () => {
        localStorage.removeItem('jwt'); // Xóa token khỏi localStorage
        setIsLoggedIn(false);
        setUsfId(''); // Reset usfId
    };

    return (
        <Router>
            <Header isLoggedIn={isLoggedIn} usfId={usfId} onLogout={handleLogout} />
            <Routes>
                <Route path="/login" element={isLoggedIn ? <Navigate to="/" /> : <Login onLogin={handleLogin} />} />
                <Route path="/register" element={<Register />} />
                <Route path="/reserve" element={<Reserve />} />
                <Route path="/" element={<Slideshow />} />
            </Routes>
        </Router>
    );
};

export default App;
