import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const Reserve = () => {
    const [date, setDate] = useState('');
    const [availableComputers, setAvailableComputers] = useState([]);
    const navigate = useNavigate();

    useEffect(() => {
        const token = localStorage.getItem('jwt');
        if (!token) {
            navigate('/login');
        }
    }, [navigate]);

    const fetchAvailableComputers = async (selectedDate) => {
        try {
            const token = localStorage.getItem('jwt');
            const response = await axios.post('/available-computers', { date: selectedDate }, {
                headers: {
                    Authorization: `Bearer ${token}`,
                },
            });
            setAvailableComputers(response.data); // Lưu danh sách máy tính khả dụng
        } catch (error) {
            console.error('Error fetching available computers:', error);
        }
    };

    const handleDateChange = (event) => {
        setDate(event.target.value);
        fetchAvailableComputers(event.target.value); // Gọi hàm để lấy máy tính khả dụng
    };

    return (
        <div>
            <h1>Reserve Page</h1>
            <input 
                type="date" 
                value={date} 
                onChange={handleDateChange} 
            />
            <h2>Available Computers:</h2>
            <ul>
                {availableComputers.map((computer) => (
                    <li key={computer.computer_id}>{computer.computer_name}</li>
                ))}
            </ul>
        </div>
    );
};

export default Reserve;
