import React, { useState } from 'react';
import axios from '../../axiosConfig'; // Đảm bảo bạn đã tạo axiosConfig.js
import './Login.css'; 

const Login = () => {
    const [showPassword, setShowPassword] = useState(false);
    const [password, setPassword] = useState('');
    const [usfEmail, setUsfEmail] = useState('');
    const [usfId, setUsfId] = useState(null); // Thêm state để lưu usf_id

    const togglePasswordVisibility = () => {
        setShowPassword(!showPassword);
    };

    const handleLogin = async (e) => {
        e.preventDefault();

        console.log('Email:', usfEmail); // Kiểm tra giá trị email
        console.log('Password:', password); // Kiểm tra giá trị password

        try {
            const response = await axios.post('/login', {
                usf_email: usfEmail, // Đảm bảo rằng tên trường là đúng
                password: password
            });
            console.log('Login successful:', response.data);

            // Giả sử response.data chứa thông tin người dùng, bao gồm usf_id
            setUsfId(response.data.usf_id); // Cập nhật usf_id vào state
        } catch (error) {
            console.error('Login failed:', error.response ? error.response.data : error.message);
        }
    };

    const handleLogout = () => {
        setUsfId(null); // Đặt usf_id về null khi đăng xuất
        // Xử lý thêm nếu cần, như xóa token khỏi local storage
    };

    const handleForgotPassword = () => {
        window.location.href = '/forgot-password'; // Hoặc bạn có thể sử dụng React Router để điều hướng
    };

    return (
        <div className="container">
            <form className="login-form" onSubmit={handleLogin}>
                <h2>Login</h2>
                <div className="form-group">
                    <label htmlFor="email">USF Email</label>
                    <input 
                        type="email" 
                        id="email" 
                        placeholder="Enter your usf email (@usf.edu)" 
                        required 
                        value={usfEmail}
                        onChange={(e) => setUsfEmail(e.target.value)}
                    />
                </div>
                <div className="form-group">
                    <label htmlFor="password">Password</label>
                    <div style={{ position: 'relative' }}>
                        <input
                            type={showPassword ? 'text' : 'password'}
                            id="password"
                            value={password}
                            onChange={(e) => setPassword(e.target.value)}
                            placeholder="Enter your password"
                            required
                        />
                        <button
                            type="button"
                            className="show-password"
                            onClick={togglePasswordVisibility}
                        >
                            {showPassword ? 'Hide' : 'Show'}
                        </button>
                    </div>
                </div>
                <button type="submit" className="login-button">Login</button>
                <div className="forgot-password">
                    <button 
                        type="button" 
                        onClick={handleForgotPassword}
                        className="forgot-password-link"
                    >
                        Forgot password?
                    </button>
                </div>
            </form>
            {usfId && (
                <div className="user-info">
                    <p>Welcome, {usfId}!</p>
                    <button onClick={handleLogout} className="logout-button">Log Out</button>
                </div>
            )}
        </div>
    );
};

export default Login;
