import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import './Header.css';
import logo from "../assets/images/logo.png";

const Header = () => {
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [usfId, setUsfId] = useState('');

  const toggleMenu = () => {
    setIsMenuOpen(prevState => !prevState);
  };

  const handleLogout = () => {
    localStorage.removeItem('jwt');
    setIsLoggedIn(false);
    setUsfId('');
  };

  useEffect(() => {
    const token = localStorage.getItem('jwt');
    if (token) {
      const userData = JSON.parse(atob(token.split('.')[1])); // Giải mã token
      setUsfId(userData.usf_id); // Giả sử rằng usf_id có trong token
      setIsLoggedIn(true);
    }
  }, []);

  return (
    <header className="header">
      <div className="hamburger" onClick={toggleMenu}>
        <div className={`bar ${isMenuOpen ? 'open' : ''}`}></div>
        <div className={`bar ${isMenuOpen ? 'open' : ''}`}></div>
        <div className={`bar ${isMenuOpen ? 'open' : ''}`}></div>
      </div>
      <div className="logo">
        <img src={logo} alt="Logo" className="logo-image" />
        <Link to="/">USF, GO BULLS</Link>
      </div>
      <nav className={`nav ${isMenuOpen ? 'open' : ''}`}>
        <ul className="nav-links">
          <li>
            <Link to="/">Home</Link>
          </li>
          <li>
            <Link to="/about">About Us</Link>
          </li>
          <li>
            <Link to="/reserve">Reserve</Link>
          </li>
        </ul>
      </nav>
      <div className="auth-buttons">
        {isLoggedIn ? (
          <>
            <span className="button">{usfId}</span>
            <button onClick={handleLogout} className="button">Logout</button>
          </>
        ) : (
          <>
            <Link to="/login" className="button">Login</Link>
            <Link to="/register" className="button">Register</Link>
          </>
        )}
      </div>
    </header>
  );
};

export default Header;
