import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import './Reserve.css'; // Ensure to import your CSS file

const Reserve = () => {
    const [availableComputers, setAvailableComputers] = useState([]);
    const [selectedDate, setSelectedDate] = useState(""); // State to store the selected date
    const [selectedComputer, setSelectedComputer] = useState(""); // State to store the selected computer
    const navigate = useNavigate();

    useEffect(() => {
        const token = localStorage.getItem('jwt');
        if (!token) {
            navigate('/login'); // Redirect to login if no token found
        }
    }, [navigate]);

    const fetchAvailableComputers = async (date) => {
        try {
            const token = localStorage.getItem('jwt');
            const response = await axios.post('http://localhost:3000/available-computers', { date }, {
                headers: {
                    Authorization: `Bearer ${token}`,
                },
            });
            const formattedComputers = response.data.map(item => ({ id: item.computer_id, name: `Computer #${item.computer_id}`, status: 'Available' }));
            console.log(formattedComputers);
            setAvailableComputers(formattedComputers); // Store the list of available computers
            setSelectedComputer(""); // Reset selected computer when fetching new availability
        } catch (error) {
            console.error('Error fetching available computers:', error);
        }
    };

    const handleDateChange = (event) => {
        const newDate = event.target.value;
        setSelectedDate(newDate); // Update the selected date
        if (newDate) {
            fetchAvailableComputers(newDate); // Automatically fetch available computers for the new date
        } else {
            setAvailableComputers([]); // Clear available computers if date is cleared
        }
    };

    const handleComputerSelect = (computer) => {
        setSelectedComputer(computer); // Update the selected computer
    };

    const handleConfirmSelection = () => {
        // Handle confirmation logic here (e.g., navigating or showing a message)
        console.log(`Selected: ${selectedComputer}`);
    };

    return (
        <div className="reserve_container">
            <h1>Reserve Page</h1>
            <form>
                <label>
                    Select Date:
                    <input type="date" value={selectedDate} onChange={handleDateChange} required />
                </label>
            </form>

            <h2>Available Computers for {selectedDate}:</h2>
            <div className="custom-dropdown">
                {availableComputers.length > 0 ? (
                    availableComputers.map((computer, index) => (
                        <div
                            key={index}
                            className={`dropdown-option ${selectedComputer === computer.name ? 'selected' : ''}`}
                            onClick={() => handleComputerSelect(computer.name)}
                        >
                            <span className="computer-name">{computer.name}</span>
                            <span className="computer-status">{computer.status}</span>
                        </div>
                    ))
                ) : (
                    <div className="dropdown-option disabled">No computers available.</div>
                )}
            </div>
            <button onClick={handleConfirmSelection} disabled={!selectedComputer}>Confirm Selection</button>
        </div>
    );
};

export default Reserve;
