// src/pages/Manage.js
import React, { useEffect, useState } from 'react';
import axios from 'axios';

const Manage = () => {
    const [reservations, setReservations] = useState([]);
    const [error, setError] = useState('');

    // Function to fetch reservations
    const fetchReservations = async () => {
        try {
            const token = localStorage.getItem('token'); // Assuming you store the JWT token in local storage
            const response = await axios.get('/manage', {
                headers: {
                    Authorization: `Bearer ${token}`,
                },
            });
            setReservations(response.data); // Adjust according to your response structure
        } catch (err) {
            setError('Failed to fetch reservations. Please try again.');
            console.error(err);
        }
    };

    // Function to delete a reservation
    const handleDelete = async (reservationId) => {
        try {
            const token = localStorage.getItem('token');
            await axios.delete(`mâ`, {
                headers: {
                    Authorization: `Bearer ${token}`,
                },
            });
            // Re-fetch reservations after deletion
            fetchReservations();
        } catch (err) {
            setError('Failed to delete reservation. Please try again.');
            console.error(err);
        }
    };

    useEffect(() => {
        fetchReservations();
    }, []);

    return (
        <div className="manage-reservation">
            <h1>Manage Your Reservations</h1>
            {error && <p style={{ color: 'red' }}>{error}</p>}
            <ul>
                {reservations.map(reservation => (
                    <li key={reservation.id}>
                        <p>Date: {reservation.date_time}</p>
                        <p>Computer ID: {reservation.computer_id}</p>
                        <button onClick={() => handleDelete(reservation.id)}>Delete</button>
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default Manage;
