import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Routes, Navigate } from 'react-router-dom';
import Login from './pages/Login/Login';
import Register from './pages/Register/Register';
import Reserve from './pages/Reserve/Reserve';
import Header from './components/Header';
import Slideshow from './components/Slideshow';
import Manage from './pages/Manage/Manage';
import NotFound from './pages/NotFound/NotFound';
import Dashboard from './pages/Dashboard/Dashboard';
// import About from './pages/About/About'; 

const App = () => {
    const [isLoggedIn, setIsLoggedIn] = useState(false);
    const [usfId, setUsfId] = useState('');
    const [role, setRole] = useState(''); // Thêm state cho role

    const handleLogin = (usfId) => {
        setIsLoggedIn(true);
        setUsfId(usfId);
    };

    const handleLogout = () => {
        localStorage.removeItem('jwt');
        setIsLoggedIn(false);
        setUsfId('');
        setRole(''); // Reset role khi đăng xuất
    };

    useEffect(() => {
        const token = localStorage.getItem('jwt');
        if (token) {
            const userData = JSON.parse(atob(token.split('.')[1]));
            setUsfId(userData.usf_id);
            setRole(userData.usf_role); // Lưu role từ token
            setIsLoggedIn(true);
        }
    }, []);

    return (
        <Router>
            <Header isLoggedIn={isLoggedIn} usfId={usfId} onLogout={handleLogout} />
            <Routes>
                <Route path="/login" element={<Login onLogin={handleLogin} />} />
                <Route path="/register" element={<Register />} />

                {/* Route cho About, Reserve, chỉ cho phép người dùng không phải admin */}
                {/* <Route path="/about" element={role !== 'admin' ? <About /> : <Navigate to="/" />} /> */}
                <Route path="/reserve" element={role !== 'admin' ? <Reserve /> : <Navigate to="/" />} />

                {/* Route cho Manage, chỉ cho phép admin */}
                <Route path="/manage" element={role === 'admin' ? <Manage /> : <Navigate to="/" />} />

                <Route path="/" element={<Slideshow />} />
                <Route path="/dashboard" element={role === 'admin' ? <Dashboard /> : <Navigate to="/" />} />
                <Route path="*" element={<NotFound />} /> 
            </Routes>
        </Router>
    );
};

export default App;
