// src/App.js
import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import Login from './pages/Login/Login';
import Register from './pages/Register/Register';
import Reserve from './pages/Reserve/Reserve';
import Header from './components/Header';
import Slideshow from './components/Slideshow'; // Import the Slideshow component

const App = () => {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [usfId, setUsfId] = useState('');

  const handleLogin = () => {
    const token = localStorage.getItem('jwt');
    if (token) {
      const userData = JSON.parse(atob(token.split('.')[1])); // Giải mã token
      setUsfId(userData.usf_id); // Giả sử rằng usf_id có trong token
      setIsLoggedIn(true);
    }
  };

  return (
    <Router>
      <Header isLoggedIn={isLoggedIn} usfId={usfId} /> {/* Truyền props vào Header */}
      <Routes>
        <Route path="/login" element={<Login onLogin={handleLogin} />} />
        <Route path="/register" element={<Register />} />
        <Route path="/reserve" element={<Reserve />} />
        <Route path="/" element={<Slideshow />} /> {/* Render Slideshow here */}
      </Routes>
    </Router>
  );
};

export default App;
