// src/App.js
import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Routes, Navigate } from 'react-router-dom';
import Login from './pages/Login/Login';
import Register from './pages/Register/Register';
import Reserve from './pages/Reserve/Reserve';
import Header from './components/Header';
import Slideshow from './components/Slideshow';
import Manage from './pages/Manage/Manage'; // Quản lý đặt chỗ của người dùng
import Admin from './pages/Admin/Admin'; // Trang Admin cho quản trị viên

const App = () => {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [usfId, setUsfId] = useState('');
  const [role, setRole] = useState('user'); // Mặc định vai trò là 'user'

  const handleLogin = (usfId, userRole) => {
    setIsLoggedIn(true);
    setUsfId(usfId);
    setRole(userRole); // Cập nhật vai trò người dùng
  };

  const handleLogout = () => {
    localStorage.removeItem('jwt');
    setIsLoggedIn(false);
    setUsfId('');
    setRole('user'); // Đặt lại vai trò về mặc định
  };

  useEffect(() => {
    const token = localStorage.getItem('jwt');
    if (token) {
      const userData = JSON.parse(atob(token.split('.')[1]));
      setUsfId(userData.usf_id);
      setIsLoggedIn(true);
      setRole(userData.role); // Giả định vai trò được lưu trong token
    }
  }, []);

  return (
    <Router>
      <Header isLoggedIn={isLoggedIn} usfId={usfId} role={role} onLogout={handleLogout} />
      <Routes>
        <Route path="/login" element={<Login onLogin={handleLogin} />} />
        <Route path="/register" element={<Register />} />
        <Route path="/reserve" element={<Reserve />} />
        {/* Route quản lý cho người dùng */}
        <Route path="/manage" element={isLoggedIn ? <Manage /> : <Navigate to="/login" />} />
        {/* Route riêng cho admin */}
        <Route path="/admin" element={role === 'admin' ? <Admin /> : <Navigate to="/" />} />
        <Route path="/" element={<Slideshow />} />
      </Routes>
    </Router>
  );
};

export default App;
